import React, { Component } from 'react';
import '../App.css';
import * as d3 from 'd3';

class Bar extends Component {


	drawChart(){
	// set the dimensions and margins of the graph
	if(!this.props.data.length){
		return;
	}
	var data = this.props.data[0].observations;

	data = data.map(function(obj, index){
		return {
			date: d3.timeParse("%Y-%m-%d")(obj.date),
			value: obj.value === '.' ? 0 : obj.value
		}
	})
	var margin = {top: 10, right: 30, bottom: 30, left: 50},
	    width = 800 - margin.left - margin.right,
	    height = 400 - margin.top - margin.bottom;

	// append the svg object to the body of the page
	var svg = d3.select("#bar_chart")
	  .append("svg")
	    .attr("width", width + margin.left + margin.right)
	    .attr("height", height + margin.top + margin.bottom)
	    .append("g")
	    .attr("transform",
	          "translate(" + margin.left + "," + margin.top + ")");

	var xScale = d3.scaleBand()
		.domain(data.map(function(d) { return d.date; }))
		.range([0, width]).padding(0.4);
	svg.append("g")
      .attr("transform", "translate(0," + height + ")")
      .call(d3.axisBottom(x).tickFormat(d => ( (d.getMonth()+1)+'/'+d.getFullYear().toString().substr(2))));

	var yScale= d3.scaleLinear()
		.domain([0, d3.max(data, function(d) { return d.value; })])
		.range([height, 0]);
	svg.append("g")
      .call(d3.axisLeft(y));

    svg.selectAll(".bar")
         .data(data)
         .enter().append("rect")
         .attr("class", "bar")
         .attr("x", function(d) { return xScale(d.year); })
         .attr("y", function(d) { return yScale(d.value); })
         .attr("width", xScale.bandwidth())
         .attr("height", function(d) { return height - yScale(d.value); });

	}

	componentDidMount(){
		this.drawChart();
	}

	componentWillReceiveProps(nextProps) {
		this.drawChart();
	}
  render() {
    return (
        <div id="bar_chart"></div>
    );
  }
}

export default Bar;
