import React, { Component } from 'react';
import Shaded from './Shaded';
import Bar from './Bar';
import Line from './Line';
import { getObservationBySeriesId } from '../services/dataService';
import config from '../config/config';

class Graph extends Component {

  constructor(props){
    super(props);
    this.state = {
      data : []
    };
  }

  getSeriesId(type){
    return config['SERIES_ID'][type]
  }

  getObservationById(seriesId){
    getObservationBySeriesId(seriesId)
      .then((data) => {
        this.setState(state => {
          const data = state.data.push(data);
          return {
            data
          };
        });
      });
  }

  componentWillMount() {
    getObservationById(this.getSeriesId(this.props.type));
  }

  renderComponent(){
    switch(this.props.type){
      case 'shaded': 
        return <Shaded data={this.state.data} seriesId={this.getSeriesId('shaded')}/>;
      case 'bar': 
        return <Bar data={this.state.data} seriesId={this.getSeriesId('bar')}/>; 
      case 'line': 
        return <Line data={this.state.data} seriesId={this.getSeriesId('line')}/>

      default:
        return 'No component to render';
    }
  }

  render() {
    return (
      this.renderComponent()
    );
  }
}

export default Graph;
