import React, { Component } from 'react';
import Shaded from './Shaded';
import Bar from './Bar';
import Line from './Line';
import { getObservationsBySeriesId } from '../services/dataService';
import config from '../config/config';

class Graph extends Component {

  constructor(props){
    super(props);
    this.state = {
      data : []
    };
  }

  getSeriesId(type){
    return config['SERIES_ID'][type]
  }

  getObservationsById(seriesId){
    return getObservationsBySeriesId(seriesId)
      .then((resp) => {
        this.setState(state => {
          state.data.push(resp);
          return state;
        });
        return resp;
      });
  }

  fetchContentByType(type){
    var ids = this.getSeriesId(type).split('-');
    ids.forEach((id) => {
      this.getObservationsById(id).then((data) => {
        console.log(data);
      });
    });
  }

  componentDidMount() {
    this.fetchContentByType(this.props.type);
  }

  componentDidUpdate() {
    this.fetchContentByType(this.props.type);
  }

  renderComponent(){
    switch(this.props.type){
      case 'shaded': 
        return <Shaded data={this.state.data} seriesId={this.getSeriesId('shaded')}/>;
      case 'bar': 
        return <Bar data={this.state.data} seriesId={this.getSeriesId('bar')}/>; 
      case 'line': 
        return <Line data={this.state.data} seriesId={this.getSeriesId('line')}/>

      default:
        return 'No component to render';
    }
  }

  render() {
    return (
      this.renderComponent()
    );
  }
}

export default Graph;
