import React, { Component } from 'react';
import '../App.css';
import * as d3 from 'd3';

class Line extends Component {

	drawChart(data){
	// set the dimensions and margins of the graph
	var that = this;
	debugger;
	if(!this.props.data.length && !data){
		return;
	}
	var data = this.props.data.length ? this.props.data : data;

	var data = data.filter(function(obsA){
		return this.props.data[1].observations.some(function(obsB){
			if(obsA.date === obsB.date){
				return {
					data : obsA.date,
					value : obsA.value - obsB.value
				}
			}
		})
	});

	console.log('refined');
	console.log(data);

	data = data.map(function(obj, index){
		return {
			date: d3.timeParse("%Y-%m-%d")(obj.date),
			value: obj.value === '.' ? 0 : obj.value
		}
	})
	var margin = {top: 10, right: 30, bottom: 50, left: 50},
	    width = 800 - margin.left - margin.right,
	    height = 400 - margin.top - margin.bottom;




	}

	componentDidMount(){
		this.drawChart();
	}

	componentWillReceiveProps(nextProps) {
		this.drawChart(nextProps.data);
	}

	render() {
		return <div id="line_chart"></div>
	}
}

export default Line;
