import React, { Component } from 'react';
import * as d3 from 'd3';
import '../App.css';

class Shaded extends Component {

	drawChart(data){
	// set the dimensions and margins of the graph
	if(!this.props.data.length){
		return;
	}
	var data = this.props.data[0].observations;

	data = data.map(function(obj, index){
		return {
			date: d3.timeParse("%Y-%m-%d")(obj.date),
			value: obj.value === '.' ? 0 : obj.value
		}
	})
	var margin = {top: 10, right: 30, bottom: 30, left: 50},
	    width = 800 - margin.left - margin.right,
	    height = 400 - margin.top - margin.bottom;

	// append the svg object to the body of the page
	var svg = d3.select("#shaded_chart")
	  .append("svg")
	    .attr("width", width + margin.left + margin.right)
	    .attr("height", height + margin.top + margin.bottom)
	    .append("g")
	    .attr("transform",
	          "translate(" + margin.left + "," + margin.top + ")");
	debugger;
    var x = d3.scaleTime()
      .domain(d3.extent(data, function(d) { return d.date; }))
      .range([ 0, width ]);
    svg.append("g")
      .attr("transform", "translate(0," + height + ")")
      .call(d3.axisBottom(x).tickFormat(d => (d.year +":")));

	debugger;
    // Add Y axis
    var y = d3.scaleLinear()
      .domain([0, d3.max(data, function(d) { return +d.value; })])
      .range([ height, 0 ]);
    svg.append("g")
      .call(d3.axisLeft(y));

    // Add the area
    svg.append("path")
      .datum(data)
      .attr("fill", "#cce5df")
      .attr("stroke", "#69b3a2")
      .attr("stroke-width", 1.5)
      .attr("d", d3.area()
        .x(function(d) { return x(d.date) })
        .y0(y(0))
        .y1(function(d) { return y(+d.value) })
      );
	}

	componentDidMount(){
		this.drawChart();
	}

	componentWillReceiveProps(nextProps) {
		this.drawChart();
	}

	render() {
		return <div id="shaded_chart"></div>
	}


}

export default Shaded;
